INSERT INTO items (name, description, photo_url, price, owner_id) VALUES ('Death Stranding', 'Death Stranding is an action game developed by Kojima Productions. It is the first game from director Hideo Kojima and Kojima Productions after their split from Konami in 2015. ... The game is set in the United States following a cataclysmic event which caused destructive creatures to begin roaming the Earth.', 'https://cdn.akamai.steamstatic.com/steam/apps/1190460/header.jpg?t=1636451066', 39.99, 1);
INSERT INTO items (name, description, photo_url, price, owner_id) VALUES ('Lost Ark', 'Lost Ark from Smilegate RPG is a 3d fantasy MMORPG with action RPG style combat and a huge world to explore. Using the Unreal engine, the game features a beautifully illustrated world of epic proportions.', 'https://steamah.com/wp-content/uploads/2022/02/Lost-Ark.jpg', 69.99, 2);
INSERT INTO items (name, description, photo_url, price, owner_id) VALUES ('Sifu', 'Sifu is a stylish yet gritty beat-em-up, featuring visceral hand-to-hand combat in a contemporary urban setting. From Sloclap, creators of acclaimed PS4 fighting game Absolver, Sifu follows a young kung fu student on a path of revenge, hunting for the murderers of his family.', 'https://cdn2.unrealengine.com/egs-sifustandardedition-sloclap-g2-00-1920x1080-edb885b62a36.jpg?h=720&resize=1&w=1280', 89.99, 2);
INSERT INTO items (name, description, photo_url, price, owner_id) VALUES ('Horizon Forbidden West', 'Horizon Forbidden West continues the story of Aloy, a young huntress of the Nora tribe, as she leads a band of companions on a quest to the arcane frontier known as the Forbidden West to find the source of a mysterious plague that kills all it infects.', 'https://608111-1969675-raikfcquaxqncofqfm.stackpathdns.com/wp-content/uploads/2022/02/horizon-forbidden-west.jpg', 79.99, 3);
